import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import React, { useState, useEffect } from 'react';






function Sidebar () {
    //States
    const [avgScore, setAvgScore] = useState(0);



    //Get selectionSoftwareProductDocumentId
    const selectionSoftwareProductDocumentId = localStorage.getItem("selectionSoftwareProductDocumentId");




    // Get AVG score via QueryString, Run once
    useEffect(() => {

        const url = `http://localhost:5001/api/contract-checker/get-contractAvgScore?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        fetch(url, {
            method: "GET",
            headers: {
            "Content-Type": "application/json",
            },
        })
        .then(response => response.text())
        .then(data => {
            setAvgScore(parseFloat(data));
            console.log(data);
        })
        .catch(error => {
            console.error('Error fetching the average score:', error);
        });

    }, []);



    //Value for Circular Progressbar
    let value = 0;
    if (avgScore) {
        value = avgScore;
    } 








    return (
        <>

        {/* Sidebar */}
        <div className="flex justify-center w-72 bg-white h-full">
            <div className="flex flex-col">
                <h1 className="mt-8 text-xl font-bold">Dashboard</h1>

                {/* Mini Dashboard */}
                <div className="mt-5 bg-white w-60 h-32 rounded-lg shadow-md border border-primary-2">
                    <div className="flex flex-grow justify-between ">
                        {/* Left Dashboard */}
                        <div className="flex flex-col ml-4">
                            <h1 className="mt-6 font-bold text-left">✅&nbsp;&nbsp;&nbsp;8/18</h1>
                            <h1 className="mt-1 font-bold text-left">❌&nbsp;&nbsp;&nbsp;3/18</h1>
                            <h1 className="mt-1 font-bold text-left">❓&nbsp;&nbsp;&nbsp;1/18</h1>
                        </div> 
                        {/* Right Dashboard */}
                        <div className="mr-4 mt-3 ">
                            <div>
                                <h1 className="ml-3 text-sm font-bold"> Score</h1>
                            </div>
                            {/* Progressbar */}
                            <div className="mt-1" style={{ width: 70, height: 70}}>
                                <CircularProgressbar 
                                styles={buildStyles({ pathColor:'#1A56DB', textColor:'#000000' })}
                                value={value} maxValue={0.8} text={`${value}`} 
                                />
                            </div>
                        </div>
                    </div>
                </div> 
                {/* AI Results */}
                <h1 className="mt-5 text-xl font-bold">AI Results</h1>
                <div className="mt-5 bg-white w-60 h-sidebar rounded-lg shadow-md border border-primary-2"> 

                {/* HERE */}
                <div className="ml-4 mt-3 font-medium">
                    <h1>Scope of Work:</h1>
                    <h1 className="mt-9">Software Features and Functionality:</h1>
                    <h1 className="mt-9">Payment Terms:</h1>
                    <h1 className="mt-9">Data Security:</h1>
                    <h1 className="mt-9">Service Level Agreements (SLAs):</h1>
                    <h1 className="mt-9">Ownership and Intellectual Property Rights:</h1>
                    <h1 className="mt-9">Contract Duration and Renewal Terms:</h1>
                    <h1 className="mt-9">Exit Strategy:</h1>
                    <h1 className="mt-9">Legal Review:</h1>
                </div>



                </div>
            </div>
        </div>

        </>
        )
    }

export default Sidebar;
