import React from 'react';

interface HeaderProps {
  onAddParticipant: () => void;
  onShowDatePicker: () => void;
  dateRange: [Date, Date];
}

const Header: React.FC<HeaderProps> = ({ onAddParticipant, onShowDatePicker, dateRange }) => {
  const [startDate, endDate] = dateRange;

  const formatDate = (date: Date | undefined): string => {
    if (!date) return '';
    const options: Intl.DateTimeFormatOptions = { 
      year: 'numeric', 
      month: '2-digit', 
      day: '2-digit' 
    };
    return date.toLocaleDateString(undefined, options);
  };

  return (
    <div className="flex justify-between items-center p-3 border-b border-secondary6 bg-secondary5">
      <div className="flex">
        <button className="p-2 text-lg cursor-pointer rounded-md">Scheduled meetings</button>
        <button className="p-2 text-lg cursor-pointer rounded-md bg-secondary1 text-white ml-3">Availability</button>
      </div>
      <div className="flex items-center">
        <button 
          className="bg-primary text-white py-2 px-4 rounded-md" 
          style={{ marginLeft: '50px' }}> 
          Publish to vendors
        </button>
        <div className="flex items-center ml-5"> 
          <label className="mr-2">Date range:</label>
          <button onClick={onShowDatePicker} className="border border-secondary6 py-1 px-2 rounded-md">
            {`${formatDate(startDate)} - ${formatDate(endDate)}`}
          </button>
        </div>
        <div className="flex items-center space-x-3 ml-5">
          <button className="border border-secondary6 py-1 px-2 rounded-md">List</button>
          <button className="border border-secondary6 py-1 px-2 rounded-md">Calendar</button>
          <button className="border border-secondary6 py-1 px-2 rounded-md">Timeline</button>
        </div>
        <button className="ml-3 bg-primary text-white rounded-full h-10 w-10 flex items-center justify-center text-2xl" 
          onClick={onAddParticipant}>+</button>
      </div>
    </div>
  );
};

export default Header;
