import { useEffect } from 'react';




interface NavbarProps {
    setAvgScore: (score: number) => void;
    setTick: (tick: number) => void;
    setCross: (cross: number) => void;
    setQuestion: (question: number) => void;
    setDate: (date: string) => void;
    date: string;
    setHasClicked: (click: boolean) => void;
    hasClicked: boolean;
    //HERE
    setIsLoading: (loading:boolean) => void;
    isLoading: boolean;

    setCompliance1: (value: string) => void;
    setExit1: (value: string) => void;
    setCost1: (value: string) => void;
    setSla1: (value: string) => void;
    setExit2: (value: string) => void;
    setCost2: (value: string) => void;
    setContractDuration1: (value: string) => void;
    setPayment1: (value: string) => void;
    setCompliance2: (value: string) => void;
    setScope1: (value: string) => void;
    setDataSec1: (value: string) => void;
    setContractDuration2: (value: string) => void;
    setSoftware1: (value: string) => void;
    setOwn1: (value: string) => void;
    setSla2: (value: string) => void;
    setScope2: (value: string) => void;
    setDataSec2: (value: string) => void;
    setLegal1: (value: string) => void;
    setLegal2: (value: string) => void;
    setOwn2: (value: string) => void;
    setPayment2: (value: string) => void;
    setSoftware2: (value: string) => void;
}

function Navbar ({
    setAvgScore, setTick, setQuestion, setCross, setDate, date, setHasClicked, hasClicked, setIsLoading, isLoading,
    setCompliance1, setExit1, setCost1, setSla1, setExit2, setCost2, setContractDuration1, setPayment1,
    setCompliance2, setScope1, setDataSec1, setContractDuration2, setSoftware1, setOwn1, setSla2,
    setScope2, setDataSec2, setLegal1, setLegal2, setOwn2, setPayment2, setSoftware2
}: NavbarProps) {

    //Date standard Value
    let dateValue = 'No date available';
    if (date) {
        dateValue = date;
    }

    //Get Doc ID
    const selectionSoftwareProductDocumentId = localStorage.getItem("selectionSoftwareProductDocumentId");





    //GET Avg Score via Query String 
    const handleGetAvgScore = async () => {
        const AvgScoreUrl = `http://localhost:5001/api/contract-checker/get-contractAvgScore?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        const responseAvg = await fetch(AvgScoreUrl, {
            method: "GET",
            headers: {
            "Content-Type": "application/json",
            },
        })
        const data = await responseAvg.text();
        //Update State
        setAvgScore(parseFloat(data));
        console.log(data);
    }

    //GET Count Catergories
    const handleGetContractClasses = async () => {
        //Variables for ✅, ❌, and ❓
        let tick = 0;
        let cross = 0;
        let questionMark = 0;

        const ContractClassesUrl = `http://localhost:5001/api/contract-checker/get-contractClasses?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        try {


            const ResponseContractClasses = await fetch(ContractClassesUrl, {
                method: "GET",
                headers: {
                "Content-Type": "application/json",
                },
            })
            const DataContractClassesText = await ResponseContractClasses.text();
            const parsedData = JSON.parse(DataContractClassesText);
            console.log("Parsed data:", parsedData);

            //Handle AI Results

            // Create your quotes object dynamically : CHAT GPT SOLUTION
            const quotes: Record<string, { quote: string; type: string; category: string }> = {};


            //item: item in the array, index: index of the current array
            parsedData.results.forEach((item:any , index:any) => {
            quotes[`quote${index + 1}`] = {
                quote: item.quote,
                type: item.type,
                category: item.category,
                };
            });


            console.log("TEST1")

            //Variables for AI Result Quotes
            const Compliance1 = `${quotes.quote1.type} ${quotes.quote1.quote}`;
            const Exit1 =  `${quotes.quote2.type} ${quotes.quote2.quote}`;
            const Cost1 = `${quotes.quote3.type} ${quotes.quote3.quote}`;
            const Sla1=  `${quotes.quote4.type} ${quotes.quote4.quote}`;
            const Exit2 =  `${quotes.quote5.type} ${quotes.quote5.quote}`;
            const Cost2 =  `${quotes.quote6.type} ${quotes.quote6.quote}`;
            const ContractDuration1 = `${quotes.quote7.type} ${quotes.quote7.quote}`;
            const Payment1 =  `${quotes.quote8.type} ${quotes.quote8.quote}`;
            const Compliance2 = `${quotes.quote9.type} ${quotes.quote9.quote}`;
            const Scope1=  `${quotes.quote10.type} ${quotes.quote10.quote}`;
            const DataSec1 =  `${quotes.quote11.type} ${quotes.quote11.quote}`;
            const ContractDuration2 =  `${quotes.quote12.type} ${quotes.quote12.quote}`;
            const Software1 = `${quotes.quote13.type} ${quotes.quote13.quote}`;
            const Own1 =  `${quotes.quote14.type} ${quotes.quote14.quote}`;
            const Sla2 = `${quotes.quote15.type} ${quotes.quote15.quote}`;
            const Scope2=  `${quotes.quote16.type} ${quotes.quote16.quote}`;
            const DataSec2 =  `${quotes.quote17.type} ${quotes.quote17.quote}`;
            const Legal1 =  `${quotes.quote18.type} ${quotes.quote18.quote}`;
            const Legal2 = `${quotes.quote19.type} ${quotes.quote19.quote}`;
            const Own2 =  `${quotes.quote20.type} ${quotes.quote20.quote}`;
            const Payment2 = `${quotes.quote21.type} ${quotes.quote21.quote}`;
            const Software2=  `${quotes.quote22.type} ${quotes.quote22.quote}`;

            //Set AI Result 
            setCompliance1(Compliance1);
            setExit1(Exit1);
            setCost1(Cost1);
            setSla1(Sla1);
            setExit2(Exit2);
            setCost2(Cost2);
            setContractDuration1(ContractDuration1);
            setPayment1(Payment1);
            setCompliance2(Compliance2);
            setScope1(Scope1);
            setDataSec1(DataSec1);
            setContractDuration2(ContractDuration2);
            setSoftware1(Software1);
            setOwn1(Own1);
            setSla2(Sla2);
            setScope2(Scope2);
            setDataSec2(DataSec2);
            setLegal1(Legal1);
            setLegal2(Legal2);
            setOwn2(Own2);
            setPayment2(Payment2);
            setSoftware2(Software2);




            //Handle Tick, Cross, ? Counter 
            //Check if the data has a "results" property that is an array.

            console.log("TEST")

            parsedData.results.forEach((item: any) => {
                console.log(item.type);
                if (item.type === "✅") {
                    tick += 1;
                } else if (item.type === "❌") {
                    cross += 1;
                } else {
                    questionMark += 1;
                }
            });

            console.log("TEST")
            console.log(tick);
            console.log(cross);
            console.log(questionMark);

            setTick(tick);
            setCross(cross);
            setQuestion(questionMark);
        } catch (error) {

            //If there is no API avaible, enable AI Analyze Button
            setHasClicked(false);

            console.log("Error:", error);
        }

    }


    //GET date of AI results creation
    const handleAiDateResult = async () => {
        const ContractRunAiDateUrl = `http://localhost:5001/api/contract-checker/get-contractRunAiDate?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        const ResponseRunAiDate = await fetch(ContractRunAiDateUrl, {
            method: "GET",
            headers: {
            "Content-Type": "application/json",
        },
    })
        const RunAiDateResult = await ResponseRunAiDate.text();
        console.log(RunAiDateResult);
        setDate(RunAiDateResult);
        setHasClicked(true);
    }



    //Get AvgScore , start once
    useEffect(() => {
        handleGetAvgScore();
        handleGetContractClasses();
        handleAiDateResult();

    }, [])


    const handleClick = async () => {
        try {
            //Disable AI Start Button
            setHasClicked(true);


            //Start AI
            setIsLoading(true);
            console.log("Start AI")
            const success = await fetch("http://localhost:5001/api/contract-checker/post-contractAiStart", {
                method: "POST",
                headers: {
                  "Content-Type": "application/json",
                },
                body: JSON.stringify({ selectionSoftwareProductDocumentId: selectionSoftwareProductDocumentId }),
            });

            //Get AvgScore if AI ran
            if (success) {
                handleGetAvgScore()
                handleGetContractClasses();
                handleAiDateResult();
            }

        } catch (error) {
            console.log("Error:", error)

        } finally {
            setIsLoading(false)
            console.log("Done")
        }
    };



    return (
        <nav className="bg-white h-16 flex items-center justify-between px-4 border-b-2 border-primary2">
            <div className="flex flex-row items-center gap-6">
                <h2 className="font-sans text-sm">Date created: {dateValue}</h2>
                <h2 className="font-sans text-sm">contract.pdf</h2>
            </div>
            <div>
                <button onClick={handleClick} disabled={isLoading || hasClicked} className={`${hasClicked ? 'bg-gray-400' : 'bg-black'} w-48 h-10 rounded-lg text-white font-sans`}>Ai Analyze</button>
            </div>
        </nav>
    )
}

export default Navbar;
