import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import React, { useState } from "react";



  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen((prev) => !prev);
  }






function Sidebar () {


    //Value for Circular Progressbar
    const value = 0.7;

    return (
        <>

        {/* Sidebar */}
        <div className="flex justify-center w-72 bg-white h-full">
            <div className="flex flex-col">
                <h1 className="mt-8 text-xl font-bold">Dashboard</h1>

                {/* Mini Dashboard */}
                <div className="mt-5 bg-white w-60 h-32 rounded-lg shadow-md border border-primary-2">
                    <div className="flex flex-grow justify-between ">
                        {/* Left Dashboard */}
                        <div className="flex flex-col ml-4">
                            <h1 className="mt-6 font-bold text-left">✅&nbsp;&nbsp;&nbsp;8/12</h1>
                            <h1 className="mt-1 font-bold text-left">❌&nbsp;&nbsp;&nbsp;3/12</h1>
                            <h1 className="mt-1 font-bold text-left">❓&nbsp;&nbsp;&nbsp;1/12</h1>
                        </div> 
                        {/* Right Dashboard */}
                        <div className="mr-4 mt-3 ">
                            <div>
                                <h1 className="ml-3 text-sm font-bold"> Score</h1>
                            </div>
                            {/* Progressbar */}
                            <div className="mt-1" style={{ width: 70, height: 70}}>
                                <CircularProgressbar 
                                styles={buildStyles({ pathColor:'#1A56DB', textColor:'#000000' })}
                                value={value} maxValue={0.8} text={`${value}`} 
                                />
                            </div>
                        </div>
                    </div>
                </div> 
                {/* AI Results */}
                <h1 className="mt-5 text-xl font-bold">AI Results</h1>
                <div className="mt-5 bg-white w-60 h-sidebar rounded-lg shadow-md border border-primary-2"> 


                <div className="accordion-container" onClick={handleToggle}>
                    {/* Header (clickable area) */}
                    <div className="accordion-header">
                        <span>Software Features and Functionality</span>

                        {/* Arrow icon (changes direction when open) */}
                        <span className={`accordion-arrow ${isOpen ? "arrow-up" : "arrow-down"}`}>
                        ▼
                        </span>
                    </div>

                    {/* Expandable content (only visible if isOpen is true) */}
                    {isOpen && (
                        <div className="accordion-content">
                        <p>
                            List of specific features and functionalities included in the ERP system. 
                            Customization options available to tailor the software to your organization's needs.
                        </p>

                        {/* Example of an action button in the expanded section */}
                        <button className="add-tag-button">+ Add tag</button>
                        </div>
                    )}
                </div>



                </div>
            </div>
        </div>

        </>
        )
    }

export default Sidebar;
