import { useEffect} from 'react';

interface CategoryCounts {
    scope: number;
    software: number;
    payment: number;
    dataSec: number;
    sla: number;
    own: number;
    contractDuration: number;
    exit: number;
    legal: number;
    compliance: number;
    cost: number;
  }

interface CountCat {
    tick: number;
    cross: number;
    question: number;
}

interface aiResults {
    Compliance1: string;
    Exit1: string;
    Cost1: string;
    Sla1: string;
    Exit2: string;
    Cost2: string;
    ContractDuration1: string;
    Payment1: string;
    Compliance2: string;
    Scope1: string;
    DataSec1: string;
    ContractDuration2: string;
    Software1: string;
    Own1: string;
    Sla2: string;
    Scope2: string;
    DataSec2: string;
    Legal1: string;
    Legal2: string;
    Own2: string;
    Payment2: string;
    Software2: string;
  }


interface NavbarProps {
    setAvgScore: (score: number) => void;
    setDate: (date: string) => void;
    date: string;
    setHasClicked: (click: boolean) => void;
    hasClicked: boolean;
    setIsLoading: (loading:boolean) => void;
    isLoading: boolean;

    setCountCat: (cat: CountCat) => void;
    setAiResults: (ai: aiResults) => void;
    setCounts: (counts: CategoryCounts) => void;

}

function Navbar ({
    setAvgScore, setDate, date, setHasClicked, hasClicked, setIsLoading, isLoading,
   setAiResults, setCounts, setCountCat
}: NavbarProps,) {

    //Date standard Value
    let dateValue = 'No date available';
    if (date) {
        dateValue = date;
    }

    //Get Doc ID
    const selectionSoftwareProductDocumentId = localStorage.getItem("selectionSoftwareProductDocumentId");





    //GET Avg Score via Query String 
    const handleGetAvgScore = async () => {
        const AvgScoreUrl = `http://localhost:5001/api/contract-checker/get-contractAvgScore?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        const responseAvg = await fetch(AvgScoreUrl, {
            method: "GET",
            headers: {
            "Content-Type": "application/json",
            },
        })
        const data = await responseAvg.text();
        //Update State
        setAvgScore(parseFloat(data));
        console.log(data);
    }

    //GET Count Catergories
    const handleGetContractClasses = async () => {
        //Variables for ‚úÖ, ‚ùå, and ‚ùì
        let tick = 0;
        let cross = 0;
        let question = 0;

        const ContractClassesUrl = `http://localhost:5001/api/contract-checker/get-contractClasses?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        try {


            const ResponseContractClasses = await fetch(ContractClassesUrl, {
                method: "GET",
                headers: {
                "Content-Type": "application/json",
                },
            })
            const DataContractClassesText = await ResponseContractClasses.text();
            const parsedData = JSON.parse(DataContractClassesText);
            console.log("Parsed data:", parsedData);



            //NEW

            type Quote = {
                quote: string;
                type: string;
                category: string;
            };

            const categorizedQuotes: Record<string, Quote[]> = {};

            // Dynamically group quotes by their category
            parsedData.results.forEach((item: Quote) => {
                if (!categorizedQuotes[item.category]) {
                    categorizedQuotes[item.category] = [];
                }
                categorizedQuotes[item.category].push(item);
            });

            // Helper to safely get quotes by category and index
            const getQuote = (category: string, index: number): string => {
                const categoryQuotes = categorizedQuotes[category];
                if (categoryQuotes && categoryQuotes[index]) {
                    return `${categoryQuotes[index].type} ${categoryQuotes[index].quote}`;
                }
                return "No data available";
            };




            console.log("üì¶ Categorized Quotes:");
            Object.entries(categorizedQuotes).forEach(([key, value]) => {
            console.log(`Category: ${key}`);
            value.forEach((quote, idx) => {
                console.log(`  [${idx}] type: ${quote.type}, quote: ${quote.quote}`);
            });
            });

            console.log(categorizedQuotes)

            // Now explicitly mapping your variables to the quotes by category/index
            const Compliance1 = getQuote("Compliance", 0);
            const Compliance2 = getQuote("Compliance", 1);

            const Exit1 = getQuote("Exit Strategy", 0);
            const Exit2 = getQuote("Exit Strategy", 1);

            const Cost1 = getQuote("Cost Breakdown", 0);
            const Cost2 = getQuote("Cost Breakdown", 1);

            const Sla1 = getQuote("Service Level Agreements (SLAs)", 0);
            const Sla2 = getQuote("Service Level Agreements (SLAs)", 1);

            const ContractDuration1 = getQuote("Contract Duration and Renewal Terms", 0);
            const ContractDuration2 = getQuote("Contract Duration and Renewal Terms", 1);

            const Payment1 = getQuote("Payment Terms", 0);
            const Payment2 = getQuote("Payment Terms", 1);

            const Scope1 = getQuote("Scope of Work", 0);
            const Scope2 = getQuote("Scope of Work", 1);

            const DataSec1 = getQuote("Data Security", 0);
            const DataSec2 = getQuote("Data Security", 1);

            const Software1 = getQuote("Software Features and Functionality", 0);
            const Software2 = getQuote("Software Features and Functionality", 1);

            const Own1 = getQuote("Ownership and Intellectual Property Rights", 0);
            const Own2 = getQuote("Ownership and Intellectual Property Rights", 1);

            const Legal1 = getQuote("Legal Review", 0);
            const Legal2 = getQuote("Legal Review", 1);



            //Set AI Result 
            setAiResults({
                Compliance1,      
                Exit1,
                Cost1,
                Sla1,
                Exit2,
                Cost2,
                ContractDuration1,
                Payment1,
                Compliance2,
                Scope1,
                DataSec1,
                ContractDuration2,
                Software1,
                Own1,
                Sla2,
                Scope2,
                DataSec2,
                Legal1,
                Legal2,
                Own2,
                Payment2,
                Software2,
              });




           //Handle Tick for each Category
           // Count ticks by filtering arrays for "‚úÖ" entries
            // Helper function to safely count ticks per category
            // Safe function to count ticks with debugging
            const countTicks = (category: string): number => {
                const categoryQuotes = categorizedQuotes[category];

                const tickCount = categoryQuotes.filter(item => item.type === "‚úÖ").length;

                console.log(`‚úÖ ${category} tickCount:`, tickCount, categoryQuotes);

                return tickCount;
            };

            // Usage clearly by category
            const scopeCount = countTicks("Scope of Work");
            const softwareCount = countTicks("Software Features and Functionality");
            const paymentCount = countTicks("Payment Terms");
            const dataSecCount = countTicks("Data Security"); 
            const slaCount = countTicks("Service Level Agreements (SLAs)");
            const ownCount = countTicks("Ownership and Intellectual Property Rights");
            const contractDurationCount = countTicks("Contract Duration and Renewal Terms");
            const exitCount = countTicks("Exit Strategy");
            const legalCount = countTicks("Legal Review");
            const complianceCount = countTicks("Compliance");
            const costCount = countTicks("Cost Breakdown");



            //Update State
            setCounts({
                scope: scopeCount,
                software: softwareCount,
                payment: paymentCount,
                dataSec: dataSecCount,
                sla: slaCount,
                own: ownCount,
                contractDuration: contractDurationCount,
                exit: exitCount,
                legal: legalCount,
                compliance: complianceCount,
                cost: costCount,
              });



            //Handle Tick, Cross, ? Counter 
            //Check if the data has a "results" property that is an array.



            parsedData.results.forEach((item: any) => {
                console.log(item.type);
                if (item.type === "‚úÖ") {
                    tick += 1;
                } else if (item.type === "‚ùå") {
                    cross += 1;
                } else {
                    question += 1;
                }
            });


            setCountCat({
                tick,
                cross,
                question: question
            })

        } catch (error) {

            //If there is no API avaible, enable AI Analyze Button
            setHasClicked(false);

            console.log("Error:", error);
        }

    }


    //GET date of AI results creation
    const handleAiDateResult = async () => {
        const ContractRunAiDateUrl = `http://localhost:5001/api/contract-checker/get-contractRunAiDate?selectionSoftwareProductDocumentId=${selectionSoftwareProductDocumentId}`;

        const ResponseRunAiDate = await fetch(ContractRunAiDateUrl, {
            method: "GET",
            headers: {
            "Content-Type": "application/json",
        },
    })
        const RunAiDateResult = await ResponseRunAiDate.text();
        console.log(RunAiDateResult);
        setDate(RunAiDateResult);
        setHasClicked(true);
    }



    //Get AvgScore , start once
    useEffect(() => {
        handleGetAvgScore();
        handleGetContractClasses();
        handleAiDateResult();

    }, [])


    const handleClick = async () => {
        try {
            //Disable AI Start Button
            setHasClicked(true);


            //Start AI
            setIsLoading(true);
            console.log("Start AI")
            const success = await fetch("http://localhost:5001/api/contract-checker/post-contractAiStart", {
                method: "POST",
                headers: {
                  "Content-Type": "application/json",
                },
                body: JSON.stringify({ selectionSoftwareProductDocumentId: selectionSoftwareProductDocumentId }),
            });

            //Get AvgScore if AI ran
            if (success) {
                handleGetAvgScore()
                handleGetContractClasses();
                handleAiDateResult();
            }

        } catch (error) {
            console.log("Error:", error)

        } finally {
            setIsLoading(false)
            console.log("Done")
        }
    };



    return (
        <nav className="bg-white h-16 flex items-center justify-between px-4 border-b-2 border-primary2">
            <div className="flex flex-row items-center gap-6">
                <h2 className="font-sans text-sm">Date created: {dateValue}</h2>
                <h2 className="font-sans text-sm">contract.pdf</h2>
            </div>
            <div>
                <button onClick={handleClick} disabled={isLoading || hasClicked} className={`${hasClicked ? 'bg-gray-400' : 'bg-black'} w-48 h-10 rounded-lg text-white font-sans`}>Ai Analyze</button>
            </div>
        </nav>
    )
}

export default Navbar;
