import Sidebar from './Sidebar'
import { useEffect, useState} from 'react';
import { Document, Page } from 'react-pdf';



interface aiResults {
    Compliance1: string;
    Exit1: string;
    Cost1: string;
    Sla1: string;
    Exit2: string;
    Cost2: string;
    ContractDuration1: string;
    Payment1: string;
    Compliance2: string;
    Scope1: string;
    DataSec1: string;
    ContractDuration2: string;
    Software1: string;
    Own1: string;
    Sla2: string;
    Scope2: string;
    DataSec2: string;
    Legal1: string;
    Legal2: string;
    Own2: string;
    Payment2: string;
    Software2: string;
  }

interface CategoryCounts {
    scope: number;
    software: number;
    payment: number;
    dataSec: number;
    sla: number;
    own: number;
    contractDuration: number;
    exit: number;
    legal: number;
    compliance: number;
    cost: number;
  }

interface CountCat {
    tick: number;
    cross: number;
    question: number;
}

interface NavbarProps {
    avgScore: number;
    isLoading: boolean;
    counts: CategoryCounts;
    aiResults: aiResults;
    CountCat: CountCat;
}

function Body({ 
    avgScore, 
    isLoading,
    CountCat,
    aiResults,
    counts,
}: NavbarProps) {




    const loadingTexts = [
        "AI is analyzing your contract...",
        "Scanning contract clauses...",
        "Verifying contract details with AI...",
        "Evaluating your contract terms...",
        "AI pipeline processing your contract...",
        "Reviewing contract compliance...",
        "Parsing contract data...",
        "AI is checking your contract...",
        "Assessing contract risks...",
        "Analyzing terms and conditions..."
      ];

    const [currentText, setCurrentText] = useState("");

    //Random text with random intervall
    useEffect(() => {
        const intervalId = setInterval(() => {
        const randomIndex = Math.floor(Math.random() * loadingTexts.length);
        setCurrentText(loadingTexts[randomIndex]);
        }, 1000);
        return () => clearInterval(intervalId);
    }, []);



    //TESTING
    const [numPages, setNumPages] = useState<number | null>(null);
    const myPdf =  '/professional-services-agreement-1.pdf'
    const onDocumentLoadSuccess = ({ numPages }: { numPages: number }) => {
        setNumPages(numPages);
      };







    return (
        <>
        {/* Loading screeen -> Turns on after pressing AI Analyze Button */}
        { isLoading && (
            <div className="flex justify-center items-center fixed inset-0 h-[100vh] bg-gray-600 bg-opacity-30">
                <div className="flex flex-col justify-center items-center"> 
                    <h1 className="text-black text-outline font-bold text-xl mb-7">{currentText}</h1>
                    <>
                    <style>{`
                        @keyframes slide {
                            0% {
                                transform: translateX(0);
                            }
                            50% {
                                transform: translateX(600px);
                            }
                            100% {
                                transform: translateX(0);
                            }
                        }
                        .animate-slide {
                            animation: slide 4s linear infinite;
                        }
                    `}</style>

                    <div className="w-[800px] relative bg-gray-100 rounded-3xl h-5 drop-shadow-lg overflow-hidden">
                        <div className="absolute left-0 top-0 bg-primary h-5 rounded-3xl w-[200px] animate-slide"></div>
                    </div>
                    </>
                        <img className="w-20 h-20 "src="/icon2.png" alt="Icon1"/>
                </div>
            </div>
        )}





        {/* Main Body */}







        <div className="bg-secondary5 h-full flex">
            {/* PDF */}

            <div style={{ width: '100%', height: '100vh', overflow: 'auto' }}>
            <Document file={myPdf} onLoadSuccess={onDocumentLoadSuccess}>
                {Array.from(new Array(numPages), (el, index) => (
                <Page key={`page_${index + 1}`} pageNumber={index + 1} />
                ))}
            </Document>
            </div>




            <div className="flex-grow flex justify-center">
                <div className="flex flex-col justify-center items-center w-pdf-w h-pdf-h mt-4 bg-white shadow-2xl">
                    <h1 className="text-2xl font-bold text-primary">NOT-A-PDF.pdf</h1>
                    <h1 className="text-2xl font-bold text-primary">NOT-A-PDF.pdf</h1>
                    <h1 className="text-2xl font-bold text-primary">NOT-A-PDF.pdf</h1>
                </div>
            </div>
            <div> 
                <Sidebar avgScore={avgScore} CountCat={CountCat} aiResults={aiResults} counts={counts}/> 
            </div>
        </div>

        <div className="bg-white w-test h-[22vh]"> </div>
        </>
    )
}

export default Body;
