import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import { Menu, MenuButton, MenuItem, MenuItems } from '@headlessui/react'
import { ChevronDownIcon } from '@heroicons/react/20/solid'



interface NavbarProps {
    avgScore: number;
    tick:number;
    cross:number;
    question:number;

}



function Sidebar ( {avgScore, tick, cross, question}:NavbarProps ) {


    //Values for Classes, Circular Progressbar



    let tickValue = 0;
    if (tick) {
        tickValue = tick;
    }
    let crossValue = 0;
    if (cross) {
        crossValue = cross;
    }

    let questionValue = 0;
    if (question) {
        questionValue = question;
    }

    let value = 0;
    if (avgScore) {
        value = avgScore;
    } 



    return (
        <>

        {/* Sidebar */}
        <div className="flex justify-center w-72 bg-white h-full">
            <div className="flex flex-col">
                <h1 className="mt-8 text-xl font-bold">Dashboard</h1>

                {/* Mini Dashboard */}
                <div className="mt-5 bg-white w-64 h-32 rounded-lg shadow-md border border-primary-2">
                    <div className="flex flex-grow justify-between ">
                        {/* Left Dashboard */}
                        <div className="flex flex-col ml-5">
                            <h1 className="mt-6 font-bold text-left">✅&nbsp;&nbsp;&nbsp;{tickValue}/22</h1>
                            <h1 className="mt-1 font-bold text-left">❌&nbsp;&nbsp;&nbsp;{crossValue}/22</h1>
                            <h1 className="mt-1 font-bold text-left">❓&nbsp;&nbsp;&nbsp;{questionValue}/22</h1>
                        </div> 
                        {/* Right Dashboard */}
                        <div className="mr-5 mt-3 ">
                            <div>
                                <h1 className="ml- text-sm font-bold"> Score</h1>
                            </div>
                            {/* Progressbar */}
                            <div className="mt-1" style={{ width: 70, height: 70}}>
                                <CircularProgressbar 
                                styles={buildStyles({ pathColor:'#1A56DB', textColor:'#000000' })}
                                value={value} maxValue={0.8} text={`${value}`} 
                                />
                            </div>
                        </div>
                    </div>
                </div> 
                {/* AI Results */}
                <h1 className="mt-5 text-xl font-bold">AI Results</h1>
                <div className="mt-5 bg-white w-64 h-sidebar rounded-lg shadow-md border border-primary-2"> 

                {/* HERE */}

                <div className="flex justify-center mt-2">
                    <Menu as="div" className="flex flex-col">
                    <div>
                        <MenuButton className="inline-flex justify-center items-center w-60 h-10 gap-x-1.5 rounded-md bg-white hover:bg-gray-100">
                        Scope of Work
                        <ChevronDownIcon aria-hidden="true" className="h-5 w-5" />
                        </MenuButton>
                    </div>
                    <MenuItems className="w-60 bg-gray-100 border border-primary2 shadow-sm">
                        {/* Divider line */}
                        <hr className="border-gray-300" />
                        <div className="flex-col justify-start ml-2 text-sm font-bold h-48"> 
                            <h1 > Quote1:</h1>
                            <h1 > Quote2:</h1>

                        </div>
                    </MenuItems>
                    </Menu>
                </div>










                {/*
                </div>
                <div className="ml-4 mt-3 font-medium">
                <h1>Scope of Work:</h1>
                <h1 className="mt-9">Software Features and Functionality:</h1>
                <h1 className="mt-9">Payment Terms:</h1>
                <h1 className="mt-9">Data Security:</h1>
                <h1 className="mt-9">Service Level Agreements (SLAs):</h1>
                <h1 className="mt-9">Ownership and Intellectual Property Rights:</h1>
                <h1 className="mt-9">Contract Duration and Renewal Terms:</h1>
                <h1 className="mt-9">Exit Strategy:</h1>
                <h1 className="mt-9">Legal Review:</h1>
                */}


                </div>
            </div>
        </div>

        </>
        )
    }

export default Sidebar;
