import Sidebar from './Sidebar'
import { useEffect, useState} from 'react';

interface NavbarProps {
    avgScore: number;
    tick: number;
    cross: number;
    question: number;
    Compliance1: any;
    Exit1: any;
    Cost1: any;
    Sla1: any;
    Exit2: any;
    Cost2: any;
    ContractDuration1: any;
    Payment1: any;
    Compliance2: any;
    Scope1: any;
    DataSec1: any;
    ContractDuration2: any;
    Software1: any;
    Own1: any;
    Sla2: any;
    Scope2: any;
    DataSec2: any;
    Legal1: any;
    Legal2: any;
    Own2: any;
    Payment2: any;
    Software2: any;
}

function Body({ 
    avgScore, 
    tick, 
    cross, 
    question, 
    Compliance1, 
    Exit1, 
    Cost1, 
    Sla1, 
    Exit2, 
    Cost2, 
    ContractDuration1, 
    Payment1, 
    Compliance2, 
    Scope1, 
    DataSec1, 
    ContractDuration2, 
    Software1, 
    Own1, 
    Sla2, 
    Scope2, 
    DataSec2, 
    Legal1, 
    Legal2, 
    Own2, 
    Payment2, 
    Software2 
}: NavbarProps) {




    const loadingTexts = [
        "AI is analyzing your contract...",
        "Scanning contract clauses...",
        "Verifying contract details with AI...",
        "Evaluating your contract terms...",
        "AI pipeline processing your contract...",
        "Reviewing contract compliance...",
        "Parsing contract data...",
        "AI is checking your contract...",
        "Assessing contract risks...",
        "Analyzing terms and conditions..."
      ];

    const [currentText, setCurrentText] = useState("");

    //Random text with random intervall
    useEffect(() => {
        const intervalId = setInterval(() => {
        const randomIndex = Math.floor(Math.random() * loadingTexts.length);
        setCurrentText(loadingTexts[randomIndex]);
        }, 1000);
        return () => clearInterval(intervalId);
    }, []);

    return (
        <>
        {/* Loading screeen -> Turns on after pressing AI Analyze Button */}
        { isLoading && (
            <div className="flex justify-center items-center fixed inset-0 h-[100vh] bg-gray-600 bg-opacity-30">
                <div className="flex flex-col justify-center items-center"> 
                    <h1 className="text-black text-outline font-bold text-xl mb-7">{currentText}</h1>
                    <>
                    <style>{`
                        @keyframes slide {
                            0% {
                                transform: translateX(0);
                            }
                            50% {
                                transform: translateX(600px);
                            }
                            100% {
                                transform: translateX(0);
                            }
                        }
                        .animate-slide {
                            animation: slide 4s linear infinite;
                        }
                    `}</style>

                    <div className="w-[800px] relative bg-gray-100 rounded-3xl h-5 drop-shadow-lg overflow-hidden">
                        <div className="absolute left-0 top-0 bg-primary h-5 rounded-3xl w-[200px] animate-slide"></div>
                    </div>
                    </>
                        <img className="w-20 h-20 "src="/icon2.png" alt="Icon1"/>
                </div>
            </div>
        )}



        <div className="bg-secondary5 h-full flex">
            {/* PDF */}
            <div className="flex-grow flex justify-center">
                <div className="flex flex-col justify-center items-center w-pdf-w h-pdf-h mt-4 bg-white shadow-2xl">
                    <h1 className="text-2xl font-bold text-primary">NOT-A-PDF.pdf</h1>
                    <h1 className="text-2xl font-bold text-primary">NOT-A-PDF.pdf</h1>
                    <h1 className="text-2xl font-bold text-primary">NOT-A-PDF.pdf</h1>
                </div>
            </div>
            <div> 
                <Sidebar 
                    avgScore={avgScore} tick={tick} cross={cross} question={question} Compliance1={Compliance1} Exit1={Exit1} Cost1={Cost1} Sla1={Sla1} Exit2={Exit2} Cost2={Cost2}
                    ContractDuration1={ContractDuration1} Payment1={Payment1} Compliance2={Compliance2} Scope1={Scope1} DataSec1={DataSec1} ContractDuration2={ContractDuration2}
                    Software1={Software1} Own1={Own1} Sla2={Sla2} Scope2={Scope2} DataSec2={DataSec2} Legal1={Legal1} Legal2={Legal2} Own2={Own2} Payment2={Payment2} Software2={Software2}
                /> 
            </div>


        </div>


        <div className="bg-secondary5 w-test h-[22vh]"> </div>

        </>
    )
}

export default Body;
