import './index.css'; 
import Logobar from './features/Logobar';
import Navbar from './features/Navbar';
import Body from './features/Body';
import { useEffect, useState } from 'react';



function App() {


  //Render once to set userId
  useEffect(() => {
    localStorage.setItem("selectionSoftwareProductDocumentId", "123");
  }, []); 

  //Shared for average score, 
  const [avgScore, setAvgScore] = useState(0);

  //State for count ✅, ❌, and ❓
  const [tick, setTick] = useState(0);
  const [cross, setCross] = useState(0);
  const [question, setQuestion] = useState(0);

  //State for createdDate
  const [date, setDate] = useState('');

  //State for click -> disable after one click permanently
  const [hasClicked, setHasClicked] = useState(false);

  //State for loading AI
  const [isLoading, setIsLoading] =  useState(false);

  //State for AI Results
  const [Compliance1, setCompliance1] = useState('');
  const [Exit1, setExit1] = useState('');
  const [Cost1, setCost1] = useState('');
  const [Sla1, setSla1] = useState('');
  const [Exit2, setExit2] = useState('');
  const [Cost2, setCost2] = useState('');
  const [ContractDuration1, setContractDuration1] = useState('');
  const [Payment1, setPayment1] = useState('');
  const [Compliance2, setCompliance2] = useState('');
  const [Scope1, setScope1] = useState('');
  const [DataSec1, setDataSec1] = useState('');
  const [ContractDuration2, setContractDuration2] = useState('');
  const [Software1, setSoftware1] = useState('');
  const [Own1, setOwn1] = useState('');
  const [Sla2, setSla2] = useState('');
  const [Scope2, setScope2] = useState('');
  const [DataSec2, setDataSec2] = useState('');
  const [Legal1, setLegal1] = useState('');
  const [Legal2, setLegal2] = useState('');
  const [Own2, setOwn2] = useState('');
  const [Payment2, setPayment2] = useState('');
  const [Software2, setSoftware2] = useState('');

  //State for CatergoryCount
  const [counts, setCounts] = useState({
    scope: 0,
    software: 0,
    payment: 0,
    dataSec: 0,
    sla: 0,
    own: 0,
    contractDuration: 0,
    exit: 0,
    legal: 0,
    compliance: 0,
    cost: 0,
  });


  console.log("TE<STTT",counts);



  return (
    <>
    <div> <Logobar /> </div>
    <div> 
      <Navbar 
      setAvgScore={setAvgScore} 
      setIsLoading={setIsLoading}
      isLoading={isLoading}
      setTick={setTick} 
      setCross={setCross} 
      setQuestion={setQuestion} 
      setDate={setDate} 
      date={date} 
      setHasClicked={setHasClicked} 
      hasClicked={hasClicked}
      setCompliance1={setCompliance1}
      setExit1={setExit1}
      setCost1={setCost1}
      setSla1={setSla1}
      setExit2={setExit2}
      setCost2={setCost2}
      setContractDuration1={setContractDuration1}
      setPayment1={setPayment1}
      setCompliance2={setCompliance2}
      setScope1={setScope1}
      setDataSec1={setDataSec1}
      setContractDuration2={setContractDuration2}
      setSoftware1={setSoftware1}
      setOwn1={setOwn1}
      setSla2={setSla2}
      setScope2={setScope2}
      setDataSec2={setDataSec2}
      setLegal1={setLegal1}
      setLegal2={setLegal2}
      setOwn2={setOwn2}
      setPayment2={setPayment2}
      setSoftware2={setSoftware2}
      setCounts={setCounts}
      /> 
    </div>
    <div> 
      <Body 
      avgScore={avgScore} 
      isLoading={isLoading}
      tick={tick} 
      cross={cross} 
      question={question} 
      Compliance1={Compliance1}
      Exit1={Exit1}
      Cost1={Cost1}
      Sla1={Sla1}
      Exit2={Exit2}
      Cost2={Cost2}
      ContractDuration1={ContractDuration1}
      Payment1={Payment1}
      Compliance2={Compliance2}
      Scope1={Scope1}
      DataSec1={DataSec1}
      ContractDuration2={ContractDuration2}
      Software1={Software1}
      Own1={Own1}
      Sla2={Sla2}
      Scope2={Scope2}
      DataSec2={DataSec2}
      Legal1={Legal1}
      Legal2={Legal2}
      Own2={Own2}
      Payment2={Payment2}
      Software2={Software2}
      /> 
    </div>
    </>
  )
}

export default App;
