import './index.css'; 
import Logobar from './features/Logobar';
import Navbar from './features/Navbar';
import Body from './features/Body';
import { useEffect, useState } from 'react';



function App() {


  //Render once to set userId
  useEffect(() => {
    localStorage.setItem("selectionSoftwareProductDocumentId", "123");
  }, []); 

  //Shared for average score, 
  const [avgScore, setAvgScore] = useState(0);

  //State for count ✅, ❌, and ❓
  const [CountCat, setCountCat] = useState({
    tick: 0,
    cross: 0,
    question: 0,
  })

  //State for createdDate
  const [date, setDate] = useState('');

  //State for click -> disable after one click permanently
  const [hasClicked, setHasClicked] = useState(false);

  //State for loading AI
  const [isLoading, setIsLoading] =  useState(false);

  //State for AI Results
  const [aiResults, setAiResults] = useState({
    Compliance1: '',
    Exit1: '',
    Cost1: '',
    Sla1: '',
    Exit2: '',
    Cost2: '',
    ContractDuration1: '',
    Payment1: '',
    Compliance2: '',
    Scope1: '',
    DataSec1: '',
    ContractDuration2: '',
    Software1: '',
    Own1: '',
    Sla2: '',
    Scope2: '',
    DataSec2: '',
    Legal1: '',
    Legal2: '',
    Own2: '',
    Payment2: '',
    Software2: '',
  });

  //State for CatergoryCount
  const [counts, setCounts] = useState({
    scope: 0,
    software: 0,
    payment: 0,
    dataSec: 0,
    sla: 0,
    own: 0,
    contractDuration: 0,
    exit: 0,
    legal: 0,
    compliance: 0,
    cost: 0,
  });





  return (
    <>
    <div> <Logobar /> </div>
    <div> 
      <Navbar 
      setAvgScore={setAvgScore} 
      setIsLoading={setIsLoading}
      isLoading={isLoading}
      setCountCat={setCountCat} 
      setDate={setDate} 
      date={date} 
      setHasClicked={setHasClicked} 
      hasClicked={hasClicked}
      setAiResults={setAiResults}
      setCounts={setCounts}
      /> 
    </div>
    <div> 
      <Body 
      avgScore={avgScore} 
      isLoading={isLoading}
      CountCat={CountCat} 
      aiResults={aiResults}
      counts={counts}
      /> 
    </div>
    </>
  )
}

export default App;
