import React from 'react';

const App: React.FC = () => {

  //TASK
  //question and chatbot function
  //after asking questions trigger handleTest
  //get stats, playlist link from express


  const handleLogin = () => {
    // Redirect the user to the backend login endpoint and send with JSON 
    window.location.href = 'http://127.0.0.1:5001/api/login';
  };



  //Should activate automatically after chat is done
  const handleTest = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5001/api/post-queries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };






  return (
    <>

      {/* Navbar */}
      <nav className="bg-gray-100 py-3 p-4 shadow-lg">
        <div className="container mx-auto">
          <span className="text-2xl font-bold">MOODIFY</span>
        </div>
      </nav>

      {/* H1 Comment */}
      <div className="flex justify-center mt-32 text-4xl font-bold">
        <h1>Mood-based Spotify playlists, powered by AI. </h1>
      </div>

      {/* Textbox */}

      <div className="flex justify-center mt-10"> 
        <div className="mt-10 bg-white w-text-w h-text-h rounded-3xl shadow-lg shadow-gray-500/50 border-2 border-black border-opacity-10">

          {/* Input Box */}
          <div className="flex justify-center mt-96">
              <div className="flex items-center bg-gray-200 w-96 h-10 rounded-lg px-4">
              <img src="/chat.png" alt="Chat Icon" className="w-6 h-6 mr-4"/>
              <input 
              type="text" 
              placeholder="Send a message..." 
              className="flex-grow bg-gray-200 placeholder-gray-600 focus:outline-none"
              />
              </div>
          </div>

          {/* Buttons */}
            <div className="flex justify-around -space-x-2 mt-2">
              <div className="bg-green-700 w-48 h-10 rounded-lg" >
              </div>

              <div className="bg-black w-48 h-10 rounded-lg" >
              </div>
            </div>







        </div>


      </div>







    </>
  );
};

export default App;
